function addClass(e,t){var e=document.getElementById(e),n=document.getElementById(t);e.onmouseover=function(){n.classList.add("curent_active_show")}}function removeClass(e){document.getElementById(e).onmouseover=function(){this.classList.add("curent_active_show")},document.getElementById(e).onmouseout=function(){this.classList.remove("curent_active_show")}}function showToast(e){document.getElementById(e).addEventListener("click",function(){alert("敬请期待")})}function route(e,t){document.getElementById(e).addEventListener("click",function(){window.location.href=t})}export{addClass,removeClass,showToast,route};